import {useState, useEffect} from "react";
import { json } from "react-router-dom";
import {swal} from "sweetalert";



const Mycart = () => {
    let [allproduct, updateproduct] = useState([]);
  
    const getProduct = () => {
      fetch("http://localhost:1234/cartlist")
        .then((response) => response.json())
        .then((productarray) => {
          updateproduct(productarray);
        });
    };
  
    useEffect(() => {
      getProduct();
    }, [1]);

    const changeQty = (product, action) =>{
          if(action == "A")
             product["qty"] = product.qty+1;
          if(action == "B")
            product["qty"] = product.qty-1;
        
            if(product.qty > 0)
            {
                let url = " http://localhost:1234/cartlist";
                let postdata = {
                   headers: {"Content-type" : "application/json"},
                    method: "POST",
                    body: JSON.stringify(product)
                }
    
                fetch(url, postdata)
                .then(response => response.json)
                .then(productdata =>{
    
                })
            }
            else{
                alert("Call delete function !!")
            }
           
    }

    return(
        <div className="container mt-4">
            <div className="row">
                <div className="col-lg-4"></div>
                <div className="col-lg-8">
                    <h3 className="text-center">
                        Items in Cart : {allproduct.length}
                    </h3>

                    <table className="table">
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Price</th>
                                <th>Quantity</th>
                                <th>Total</th>
                                <th>Photo</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                 allproduct.map((product, index)=>{
                                   return(
                                    <tr key={index}>
                                        <td>{product.name} </td>
                                        <td>{product.price}</td>
                                        <td className="input-group"> 
                                        <button className="btn btn-info btn-sm" onClick={changeQty.bind(this, product, "A")}> <i className="fa fa-plus"></i></button>
                                        <input type="text" className="form-control" value={product.qty} readOnly size="5"/>
                                        <button className="btn btn-warning  btn-sm" onClick={changeQty.bind(this, product, "B")}><i className="fa fa-minus" ></i></button>
                                         </td>
                                        <td>{ product.qty * product.price}</td>
                                        <td><img src={product.photo}  height="50" width="50" /></td>
                                    </tr>
                                   )
                                 })
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
  
}

export default Mycart;
