import {useState, useEffect} from "react";
 
const Myorder = () =>{
   
    let [orderlist, updateOrder] = useState([]);
    const getOrder = () =>{
        fetch("http://localhost:1234/orderlist")
        .then(response => response.json())
        .then(data => {
            updateOrder(data.reverse());
            // recent order will come at top
        })
    }

    useEffect(()=>{
        getOrder();
    }, [1])

  return(
    <div className="container mt-5">
        <div className="row">
            <div className="col-lg-12 text-center">
                <h1 className="text-info">
                     <i className="fa fa-phone"></i>  Recent Order List: {orderlist.length} 
                 </h1>
            </div>
        </div>

        {
             orderlist.map((order,index) =>{
                return(
                    <div className="row mb-5 p-4 shadow-lg" key={index}>
                        <div className="col-lg-3">  Customer Name: {order.customername} </div> 
                        <div className="col-lg-3">Mobile no. : {order.mobile} 
                         </div> 
                        <div className="col-lg-3">E-mail: {order.email} </div> 
                         <div className="col-lg-3">Deliver To : {order.address} </div>    
                        
                        <div className="col-lg-12 mt-4"> 
                           <table className="table">
                               <thead>
                                 <th>Product</th>
                                 <th>Quantity</th>
                                 <th>Price</th>
                                 <th>Total</th>
                                 <th>Photo</th>
                               </thead>

                               <tbody>
                                {
                                    
                                }
                               </tbody>
                           </table>
                        </div>
                     </div>


                )
             })
        }
    </div>
  )
}

export default Myorder;
