import {useState, useEffect } from "react";

const Myhome = () =>{
    let[allproduct, updateproduct] = useState([]);

    const getProduct = () =>{
        fetch("http://localhost:1234/productlist")
        .then(response => response.json())
        .then(productarray => {
           updateproduct(productarray);
        })
    }

    useEffect(() =>{
      getProduct();
    }, [1])

     const addtoCart = (productinfo) =>{
           productinfo["qty"] = 1;
          let url = "http://localhost:1234/cartlist";
          let postdata = {
            headers: {'Content-Type': 'application/json'},
            method: "POST",
            body: JSON.stringify(productinfo)
          }
          fetch(url)
          .then(response => response.json())
          .then(servers => {
            
          })
     }

    return(
        <section>
            <div id="banner"> </div>
            <div className="container mt-4">
                <div className="row">
                    {
                    allproduct.map((product, index)=>{
                        return(
                            <div className="col-lg-3 mb-4" key={index}>
                             <div className="p-3 text-center shadow">
                                <h4 className="text-info mb-3"> {product.name} </h4>
                                <img src={product.photo} className="rounded" height="140" width="100%"/>
                                <p className="mt-3"> {product.details} </p>
                                <p className="m-3">Rs. {product.price} </p>
                                <p className="text-center">
                                    <button className="btn btn-danger" onClick={addtoCart.bind(this, product)}><i className="fa fa-shopping-cart"></i>Add to cart</button>
                                </p>
                             </div>
                             
                            </div>
                        )
                    })
                 
            }
            </div>
            </div>
        </section>
    )
}
export default Myhome;
