import { useState, useEffect } from "react";
import { json } from "react-router-dom";
import swal from "sweetalert";

const Mycart = () => {
  let [allproduct, updateproduct] = useState([]);

  const getProduct = () => {
    fetch("http://localhost:1234/cartlist")
      .then((response) => response.json())
      .then((productarray) => {
        updateproduct(productarray);
      });
  };

  useEffect(() => {
    getProduct();
  }, [1]);

  const changeQty = async (product, action) => {
    if (action == "A") product["qty"] = product.qty + 1;
    if (action == "B") product["qty"] = product.qty - 1;

    if (product.qty > 0) {
      let url = " http://localhost:1234/cartlist/" + product.id;
      let postdata = {
        headers: { "Content-type": "application/json" },
        method: "PUT",
        body: JSON.stringify(product),
      };

      await fetch(url, postdata)
        .then((response) => response.json)
        .then((productdata) => {
          swal(product.name, "Quantity updated in Cart !", "success");
          getProduct(); //reload the list with updated value
        })
        .catch((error) => {
          swal("Error", "while Updating quantity", "error");
        });
    } else {
      delitem(product.id, product.name);
    }
  };

  const delitem = async (id, name) => {
    let url = " http://localhost:1234/cartlist/" + id;
    let postdata = { method: "DELETE" };
    await fetch(url, postdata)
      .then((response) => response.json)
      .then((data) => {
        swal(name + "", "Deleted Successfully !!", "success");
        getProduct(); // reload the list with updated value
      });
  };

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-lg-4"></div>
        <div className="col-lg-8">
          <h3 className="text-center">Items in Cart : {allproduct.length}</h3>

          <table className="table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
                <th>Photo</th>
                <th className="text-center">Action</th>
              </tr>
            </thead>
            <tbody>
              {allproduct.map((product, index) => {
                return (
                  <tr key={index}>
                    <td>{product.name} </td>
                    <td>{product.price}</td>
                    <td className="input-group">
                      <button
                        className="btn btn-info btn-sm"
                        onClick={changeQty.bind(this, product, "A")}
                      >
                        {" "}
                        <i className="fa fa-plus"></i>
                      </button>
                      <input
                        type="text"
                        className="form-control"
                        value={product.qty}
                        readOnly
                        size="1"
                      />
                      <button
                        className="btn btn-warning  btn-sm"
                        onClick={changeQty.bind(this, product, "B")}
                      >
                        <i className="fa fa-minus"></i>
                      </button>
                    </td>
                    <td>{product.qty * product.price}</td>
                    <td>
                      <img src={product.photo} height="50" width="50" />
                    </td>

                    <td className="text-center">
                      <i
                        className="fa fa-2x fa-trash fa-lg text-danger"
                        onClick={delitem.bind(this, product.id, product.name)}
                      ></i>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Mycart;
